import { Component, OnInit } from '@angular/core';
import { CrudService } from 'src/app/services/crud.service';
import { getStorage, ref, getDownloadURL } from "firebase/storage";
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  storage = getStorage();
  data:any={
    img:'normal',
    id:'',
    link:''
  }
  value =''
  fallback = 'data:image/png;base64,';
  needVImages:any = []

  waiting_ver =0

  index =0
  constructor (private crud:CrudService, private http: HttpClient){

  }

  ngOnInit(): void {
    // this.callAPI()
    this.getImages()
  }

  // async callAPI(){
  //   fetch('https://api-lovat-nine.vercel.app/hello')
  //     .then(response => response.text())
  //     .then(data => {
  //       this.data = data;
  //       console.log(data);
  //     })
  //     .catch(error => {
  //       console.error('Error:', error);
  //     });
  // }

  async getImages(){
    await this.crud.getImages().subscribe((images)=>{
     this.needVImages = []
     console.log(images);
     images.forEach((image)=>{
       if (image?.label=='')
         this.needVImages.push(image)
     })

     this.waiting_ver = this.needVImages.length
     if (this.waiting_ver>0)this.nextData();

   })
 }

 getRandomInt(min:any, max:any) {
  const minCeiled = Math.ceil(min);
  const maxFloored = Math.floor(max);
  return Math.floor(Math.random() * (maxFloored - minCeiled) + minCeiled);
}

  nextData(){
    if (this.index<this.needVImages.length){
    this.data = this.needVImages[this.index++];
    const storageRef = ref(this.storage, `image-handwritten/${this.data.link}`);
    getDownloadURL(storageRef)
  .then((url) => {
    this.data.img = url
  })
  .catch((error) => {
    // Handle any errors
  });
    }
    else{
      if (this.needVImages.length==0){
        this.data={
          img:'normal',
          link:''
        }
      }
      else{
        this.index=0
      }
    }
  }
  async confirm(){

    if (this.value.length>0)
      {
        await this.crud.updateData("images",this.data.id,{label:this.value})
        this.value = ''
      }
  }
}
